const axios = require('axios')

const requestOne = axios.get("api.openweathermap.org/data/2.5/weather?q=Tunis&appid=e91d8c128cd1fc29f54e1c78db6b1ec3");
const requestTwo = axios.get("api.openweathermap.org/data/2.5/weather?lat=36.806112&lon=10.171078&appid=e91d8c128cd1fc29f54e1c78db6b1ec3");


axios
.all([requestOne,requestTwo])
.then(axios.spread((firstResponse,secondResponse) => {
    console.log(firstResponse.data,secondResponse.data);
    })
)
.catch(errors => {
    console.error(errors);
});